mapper.select:
select articleno, userid, subject, content,
		case when date_format(regtime, '%Y%m%d') = date_format(now(), '%Y%m%d')
		then date_format(regtime, '%H:%i:%s')
		else date_format(regtime, '%y.%m.%d')
		end regtime
from catdog


		where subject like concat('%', #{word}, '%')


		where ${key} = #{word}


order by articleno desc
limit #{start}, #{spp}

select count(articleno) from catdog
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select articleno, userid, subject, content, regtime
from catdog
where articleno = #{articleno}

mapper.update:
update catdog
set subject = #{subject}, content = #{content}
where articleno = #{articleNo}
